public ListNode removeElements(ListNode head, int val) {
        ListNode dummy = new ListNode(0);
        dummy.next = head;
        head = dummy;
        while(head.next != null) {
            if(head.next.val == val)
                head.next = head.next.next;
            else
                head = head.next;
        }
        return dummy.next;
    }

class Solution {
    public ListNode removeElements(ListNode head, int val) {
        if(head == null)
            return null;
        ListNode res = removeElements(head.next, val);
        if(head.val == val) {
            return res;
        }else {
            head.next = res;
            return head;
        }
    }
}
