    public List<Integer> findDuplicates(int[] nums) {
        List<Integer> res = new ArrayList<>();

        int[] seen = new int[nums.length];

        for(int i = 0; i < nums.length; i++) {
            seen[nums[i] - 1]++;
        }

        for(int i = 0; i < seen.length; i++) {
            if(seen[i] >= 2)
                res.add(i + 1);
        }

        return res;
    }
