            res[l2-i] = (char)(carry + num2.charAt(l2-i-1));
            if(res[l2-i]>'9'){
                carry=1;
                res[l2-i]-=10;                
            }else{
                carry=0;
            }
        }
        res[0] = (char)(carry+'0');
        String result = String.valueOf(res);
        if(res[0]=='0') return result.substring(1);
        else return result;
    }
}
