public int calculate(String s) {
        Stack<Integer> stack = new Stack<Integer>();
        int result = 0;
        int number = 0;
        int sign = 1;
        for(int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if(Character.isDigit(c)) {
                number = 10 * number + (int)(c  - '0');
            }
            else if(c == '+') {
                number = sign * number;
                result += number;
                number = 0;
                sign = 1;
            } else if(c == '-') {
                number = sign * number;
                result += number;
                number = 0;
                sign = -1;
            } else if( c == '(') {
                stack.push(result);
                stack.push(sign);
                result = 0;
                sign = 1;
            } else if( c == ')') {
                number = sign * number;
                result += number;
                number = 0; 
                result *= stack.pop();
                result += stack.pop();
            }
        }
        //ÊúÄÂêé‰ª•Êï∞Â≠óÁªìÂ∞æÁöÑËØù
        if(number != 0) return result += sign * number;
        return result;
    }
