class Solution {
    public Node copyRandomList(Node head) {
        if(head == null)
            return null;

        Map<Node, Node> map = new HashMap<>();

        Node node = head;
        while(node != null) {
            map.put(node, new Node(node.val));
            node = node.next;
        }

        node = head;
        while(node != null) {
            //Ê≥®ÊÑèËøô‰∏§Âè•ÔºÅÔºÅÔºÅÔºÅÔºÅÔºÅ
            map.get(node).next = map.get(node.next);
            map.get(node).random = map.get(node.random);
            node = node.next;
        }

        return map.get(head);
    }
}
