class Solution {
    public Node copyRandomList(Node head) {
        if(head == null)
            return null;
        Node node = head;
        while(node != null) {
            Node next = node.next;
            node.next = new Node(node.val);
            node.next.next = next;
            node = next;
        }

        node = head;
        while(node != null) {
            if(node.random != null) {
                node.next.random = node.random.next;
            }
            node = node.next.next;
        }

        node = head;
        Node copyHead = head.next;
        Node copy = copyHead;
        while(copy.next != null) {
            node.next = node.next.next;
            node = node.next;

            copy.next = copy.next.next;
            copy = copy.next;
        }
        node.next =node.next.next;

        return copyHead;
    }
}
