class Solution {
    //ÂÖ®Â±ÄÊúÄÂ§ßÂÄºÔºåÊúÄÁªàËøîÂõûÁöÑÂÄº
    int max = Integer.MIN_VALUE;

    public int maxPathSum(TreeNode root) {
        helper(root);
        return max;
    }

    //ËøîÂõûÂåÖÊã¨Ëøô‰∏™rootÂú®ÂÜÖÁöÑÊúÄÂ§ßË∑ØÂæÑÔºå3‰∏≠ÈÄâÊã©ÔºörootÊú¨Ë∫´Ôºåroot+leftÁöÑÂÄºÔºåroot+rightÁöÑÂÄº
    public int helper(TreeNode root) {
        if(root == null)
            return 0;

        //ÈªòËÆ§ÈÉΩ‰ºöÊúâËøîÂõûÂÄºÔºåÊúÄÂ∞èÂÄº‰∏∫0
        int left = Math.max(0, helper(root.left));
        int right = Math.max(0, helper(root.right));

        //Êõ¥Êñ∞max
        max = Math.max(max, root.val + left + right);
        return root.val+Math.max(left,right);//Âè™ËÉΩËøîÂõû‰∏ÄËæπÔºåËÄå‰∏îÂåÖÊã¨root
    }
}
