class Solution {
    public List<String> binaryTreePaths(TreeNode root) {
        List<String> res = new ArrayList<>();
        //dfs(res, "", root);
        helper(res, new StringBuilder(), root);
        return res;
    }

    //‰ΩøÁî®StringÔºåÂõ†‰∏∫ËøôÈáåÈÉΩÊòØÁ¨¶Âè∑Â∫îÁî®ÁîüÊàêÊñ∞ÁöÑStringÔºàimmutableÔºâ
    public void dfs(List<String> res, String temp, TreeNode root) {
        if(root == null)
            return;

        if(root.left == null && root.right == null) {
            res.add(temp + root.val);
        }
        dfs(res, temp+root.val+"->", root.left);
        dfs(res, temp+root.val+"->", root.right);
    }

    //‰ΩøÁî®StringBuilderÁöÑËØùÔºåÊìç‰ΩúÁöÑÈÉΩÊòØÂêå‰∏Ä‰∏™StringBuilderÔºå
