    public static int[] countingSort(int[] A) {
        int[] B = new int[A.length];
        // ÂÅáËÆæA‰∏≠ÁöÑÊï∞ÊçÆa'ÊúâÔºå0<=a' && a' < kÂπ∂‰∏îk=100
        int k = 100;
        countingSort(A, B, k);
        return B;
    }

    private static void countingSort(int[] A, int[] B, int k) {
        int[] C = new int[k];
        // ËÆ°Êï∞
        for (int j = 0; j < A.length; j++) {
            int a = A[j];
            C[a] += 1;
        }
        Utils.print(C);
        // Ê±ÇËÆ°Êï∞Âíå
        for (int i = 1; i < k; i++) {
            C[i] = C[i] + C[i - 1];
        }
