import java.util.Comparator;
import java.util.PriorityQueue;
import java.util.Scanner;

class Tal {
    int h, index;

    Tal(int h, int index) {
        this.h = h;
        this.index = index;
    }
}

public class Main {
    /**
     * ÊÄùÊÉ≥ÔºöÂÄüÂä©Â§ßÂ∞è‰ºòÂÖàÈòüÂàóÊØèÊ¨°ÂèñÂá∫ÊúÄÈ´òÂ°îÂíåÊúÄ‰ΩéÁöÑÂ°îËøõË°åÊìç‰ΩúÔºåÊìç‰ΩúÂÆå‰πãÂêéÂÜçÊîæÂõûÂéª
     * Áõ¥Ëá≥Âá∫Áé∞ÊúÄÂ§ßÊúÄÂ∞èÂ°îÁöÑÈ´òÂ∫¶Â∑Æ‰∏∫1ÊàñËÄÖÂèØÁî®ÁöÑÁßªÂä®Ê¨°Êï∞Áî®ÂÆå„ÄÇ
     */
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();//Â°îÁöÑÊï∞Èáè
        int k = sc.nextInt();//ÊúÄÂ§öÊìç‰ΩúÊ¨°Êï∞
        PriorityQueue<Tal> minque = new PriorityQueue<>(n, new Comparator<Tal>() {
            @Override
            public int compare(Tal o1, Tal o2) {
                return o1.h - o2.h;
            }
        });
        PriorityQueue<Tal> maxque = new PriorityQueue<>(n, new Comparator<Tal>() {
            @Override
            public int compare(Tal o1, Tal o2) {
                return o2.h - o1.h;
            }
        });
        Tal tal = null;
        for (int i = 1; i <= n; i++) {
            int t = sc.nextInt();
            tal = new Tal(t, i);
            minque.add(tal);
            maxque.add(tal);
        }
        int s = 0;//ÊúÄÂ∞èÁ®≥ÂÆöÂÄº
        int times = 0;//ÁßªÂä®Ê¨°Êï∞
        //ËÆ∞ÂΩïÁßªÂä®ÁöÑÁ¥¢Âºï
        int[][] move = new int[k][2];
        while (times < k) {
            Tal min = minque.poll();//ÊúÄ‰ΩéÁöÑÂ°î
            Tal max = maxque.poll();//ÊúÄÈ´òÁöÑÂ°î
            //Â¶ÇÊûúÊúÄ‰ΩéÂ°îÂíåÊúÄÈ´òÂ°îÈ´òÂ∫¶Áõ∏Á≠âÊàñËÄÖÁõ∏Â∑Æ‰∏∫1ÔºåÂ∞±Ê≤°ÂøÖË¶ÅÁªßÁª≠‰∫Ü
            if (max.h - min.h <= 1) {
                break;
            }
            //ÊúÄ‰ΩéÂ°îÈ´òÂ∫¶Â¢ûÂä†1ÔºåÊúÄÈ´òÂ°îÈ´òÂ∫¶ÂáèÂ∞ë1
            min.h = min.h + 1;
            max.h = max.h - 1;
            move[times][0] = max.index;
            move[times][1] = min.index;
            minque.add(min);
            maxque.add(max);
            times++;
        }
        s = maxque.peek().h - minque.peek().h;
        System.out.println(s + " " + times);
        for (int i = 0; i < times; i++) {
            System.out.println(move[i][0] + " " + move[i][1]);
        }
    }
}
```

