class Solution {
    public void sortColors(int[] nums) {
        int zeroIndex = 0, twoIndex = nums.length - 1;
        for(int i = 0; i <= twoIndex;) {
            if(nums[i] == 0) {
                nums[i] = nums[zeroIndex];
                nums[zeroIndex] = 0;
                i++;
                zeroIndex++;
            }else if(nums[i] == 2) {
